        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1339656, 'Gemtuzumab ozogamicin', 'Clozapine', 'Major', 'Coadministration of clozapine with other agents that can cause neutropenia or agranulocytosis may increase the risk and/or severity of hematologic toxicity.', NULL, 'Caution and close monitoring are advised when clozapine is used with other agents that have a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function.', NULL, 'Caution and close monitoring are advised when clozapine is used with other agents that have a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function. Agents that may be significantly myelotoxic include antineoplastic drugs, some anticonvulsant and antirheumatic medications, albendazole, chloramphenicol, colchicine, dapsone, interferons, linezolid, pentamidine, procainamide, and zidovudine.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
