        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1339660, 'Gemtuzumab ozogamicin', 'Dimethyl fumarate', 'Moderate', 'The use of monomethyl fumarate (MMF) or its prodrugs, dimethyl fumarate and diroximel fumarate, with other immunosuppressive or myelosuppressive agents may increase the risk of infections.', NULL, 'The safety and efficacy of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated.', NULL, 'The safety and efficacy of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated. Until further information is available, concomitant use should be avoided if possible.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
