    ) VALUES (1339854, 'Dronedarone', 'Alfuzosin', 'Major', 'Dronedarone may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of dronedarone with other drugs that can prolong the QT interval is considered contraindicated.', NULL, 'Coadministration of dronedarone with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
