INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (26634, 'Fenofibrate', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity such as fenofibrate may potentiate the risk of liver injury. Coadministration with lomitapide has been shown to modestly reduce the bioavailability of fenofibric acid from fenofibrate. The mechanism of interaction has not been described.', NULL, 'Caution is advised if lomitapide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents, and vice versa.', NULL, 'Caution is advised if lomitapide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents, and vice versa. Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin monitored prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Acetylsalicylic acid, Bempedoic acid, Indapamide, Valsartan, Gemfibrozil, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Lomitapide, More', 'Fenofibric acid, Evinacumab, Evolocumab, Probucol, Dextrothyroxine, Clofibrate, Fenofibrate, Bempedoic acid, Colesevelam, Alirocumab', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26666/', '[1] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (26635, 'Fenofibrate', 'Lonafarnib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of lonafarnib. In vitro, lonafarnib has been shown to be primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 1A2, 2A6, 2C8, 2C9, 2C19, and 2E1. Increased exposure to lonafarnib may increase the risk and/or severity of adverse effects such as nausea, vomiting, diarrhea, anorexia, electrolyte disturbances, liver enzyme elevations, myelosuppression, infection, and hypertension.', NULL, 'The manufacturer recommends avoiding concomitant use of lonafarnib with CYP450 2C9 inhibitors.', NULL, 'The manufacturer recommends avoiding concomitant use of lonafarnib with CYP450 2C9 inhibitors. If coadministration is required, patients should be closely monitored for common adverse effects as well as arrhythmias and events such as syncope and heart palpitations, since the effect of increased exposures on the QT interval is unknown.', 'Metabolism', 'Ramipril, Valsartan, Rosuvastatin, Lisinopril, Perindopril, Niacin, Pravastatin, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, More', 'Teduglutide, Fosdenopterin, Miglustat, Imiglucerase, Asfotase alfa, Glycerol phenylbutyrate, Pegvaliase, Triethylenetetramine, Sapropterin, Zinc acetate, Phenylbutyric acid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26667/', '[1] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.[2] "Product Information. Zokinvy (lonafarnib)." Eiger BioPharmaceuticals, Palo Alto, CA.', 1767369485);
