INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (26675, 'Fenofibrate', 'Telbivudine', 'Moderate', 'Prolonged use of certain nucleoside reverse transcriptase inhibitors may commonly cause myopathy, including rare cases of rhabdomyolysis. It is not known if the risk of myopathy is increased during concurrent administration of other agents associated with myopathy, including corticosteroids, fibric acid derivatives, HMG-CoA reductase inhibitors, chloroquine, hydroxychloroquine, penicillamine, and lipid-lowering doses of niacin.', NULL, 'Clinicians should carefully consider the benefits versus risks prior to prescribing these agents together.', NULL, 'Clinicians should carefully consider the benefits versus risks prior to prescribing these agents together. Patients should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever.', 'Synergism', 'Amlodipine, Ramipril, Valsartan, Lisinopril, Perindopril, Acetylsalicylic acid, Ezetimibe, Bempedoic acid, Indapamide, Mipomersen, Evinacumab, More', 'Paritaprevir, Glecaprevir, Tenofovir alafenamide, Maraviroc, Amprenavir, Simeprevir, Zanamivir, Cabotegravir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/26707/', '[1] "Product Information. Tyzeka (telbivudine)." IDEC Pharmaceuticals Corporation, San Diego, CA.[2] "Product Information. Retrovir (zidovudine)." Glaxo Wellcome, Research Triangle Park, NC.', 1767369485);
