INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (26717, 'Drotrecogin alfa', 'Antithrombin III human', 'Major', 'Coadministration of drotrecogin alfa and other drugs that interfere with coagulation or platelet function may potentiate the risk of bleeding complications. Drotrecogin alfa inactivates blood clotting factors Va and VIIIa and may prolong the activated partial thromboplastin time (APTT).', NULL, 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who are currently receiving, or have recently received, agents that could interfere with coagulation such as salicylates, NSAIDs, low molecular weight heparins, thrombolytics, thrombin inhibitors, platelet inhibitors, or agents that commonly cause thrombocytopenia.', NULL, 'The potentially increased risk of bleeding versus the benefits of drotrecogin alfa therapy should be carefully considered in seriously ill septic patients who are currently receiving, or have recently received, agents that could interfere with coagulation such as salicylates, NSAIDs, low molecular weight heparins, thrombolytics, thrombin inhibitors, platelet inhibitors, or agents that commonly cause thrombocytopenia. Close clinical and laboratory monitoring for bleeding complications is recommended if concurrent therapy is required. Drotrecogin alfa should be discontinued immediately if clinically significant bleeding occurs.', 'Synergism', 'Clopidogrel, Cilostazol, Lepirudin, Acenocoumarol, Urokinase, Dipyridamole, Warfarin, Argatroban, Streptokinase, Eptifibatide, Selexipag, More', 'Acenocoumarol, Selexipag, Drotrecogin alfa', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https: