INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (26993, 'Smallpox (Vaccinia) Vaccine, Live', 'Antithymocyte immunoglobulin (rabbit)', 'Major', 'The administration of live smallpox virus vaccine during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Routine, nonemergency smallpox vaccination is contraindicated in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'Routine, nonemergency smallpox vaccination is contraindicated in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued for at least 3 months in most cases. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide).', 'Others', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27025/', '[1] "Product Information. Dryvax (smallpox vaccine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] CDC. Centers for Disease Control and Prevention/ "Recommendations of the advisory committtee on immunization practices (ACIP): use of vaccines and immune globulins in persons with altered immunocompetence." MMWR Morb Mortal Wkly Rep 42(RR-04) (1993): 1-18[4] CDC. Centers for Disease Control and Prevention "Smallpox vaccination clinic guide. Logistical considerations and guidance for state and local planning for emergency, large-scale, voluntary administration of smallpox vaccine in response to a smallpox outbreak. Available from: URL: http://www.bt.cdc.gov" ([2002 Oct 16]):', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (26994, 'Talimogene laherparepvec', 'Antithymocyte immunoglobulin (rabbit)', 'Major', 'Talimogene laherparepvec is a live, attenuated herpes simplex virus. Administration during immunosuppressant or intense antineoplastic therapy may be associated with a risk of potentially life-threatening disseminated herpetic infection due to enhanced virus replication in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', NULL, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, 'Talimogene laherparepvec should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Treatment with talimogene laherparepvec may need to be deferred until after such therapy is discontinued and the patient''s immune system has sufficiently recovered.', 'Synergism', 'Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', 'Pertuzumab, Olaparib, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27026/', '[1] "Product Information. Imlygic (talimogene laherparepvec)." Amgen USA, Thousand Oaks, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (26995, 'Teriflunomide', 'Antithymocyte immunoglobulin (rabbit)', 'Major', 'The use of leflunomide with other immunosuppressive or myelosuppressive agents may increase the risk of infections. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide. Serious infections including sepsis, as well as opportunistic infections like Pneumocystis jiroveci pneumonia, pulmonary and extrapulmonary tuberculosis, and aspergillosis have been reported with the use of leflunomide, particularly in patients on concomitant hematotoxic therapy. Agents that may be significantly immuno- or myelosuppressive include antineoplastic agents, radiation, zidovudine, linezolid, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents , and long-term topical or inhaled corticosteroids. Rare cases of pancytopenia, agranulocytosis, and thrombocytopenia have also occurred with leflunomide alone, but were most frequent in the presence of concomitant or recent use of methotrexate or other myelotoxic agents. Due to the prolonged elimination half-life of leflunomide''s active metabolite, an interaction may occur even when hematotoxic agents are initiated after the discontinuation of leflunomide. Administering a washout procedure with cholestyramine or activated charcoal helps to accelerate elimination of the active metabolite from plasma and reduce the overlap of systemic exposure to these agents.', NULL, 'Close monitoring for the development of infection is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immuno- or myelosuppressive agents, and vice versa.', NULL, 'Close monitoring for the development of infection is recommended if leflunomide or teriflunomide is used in patients who are currently receiving or have recently received other immuno- or myelosuppressive agents, and vice versa. Platelet, white blood cell count, and hemoglobin or hematocrit should be evaluated at baseline and regularly during therapy. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. If evidence of serious infection or bone marrow suppression occurs, treatment should be stopped, and cholestyramine or charcoal administered to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years.', 'Synergism', 'Teriflunomide, Apremilast, Belumosudil, Teprotumumab, Emapalumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', 'Cyclosporine, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Anifrolumab, Ravulizumab, Mycophenolic acid, Methotrexate, Tacrolimus, Abatacept, Apremilast, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/27027/', '[1] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] "Product Information. Aubagio (teriflunomide)." Genzyme Corporation, Cambridge, MA.', 1767369485);
