INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55145, 'Cannabidiol', 'Theophylline', 'Moderate', 'Concomitant use of cannabis (marijuana) may decrease the plasma concentrations of theophylline. Smoking marijuana has been reported to increase theophylline clearance, with effect similar to that of smoking tobacco. The mechanism of interaction has not been established, but may involve induction of CYP450 1A2-mediated metabolism by the cannabinoids in marijuana. In vitro data suggest that delta-9-tetrahydrocannabinol may induce CYP450 1A2, while cannabidiol may induce or inhibit CYP450 1A2 at clinically relevant concentrations.', NULL, 'Theophylline levels should be monitored closely in patients who use marijuana or its cannabinoids.', NULL, 'Theophylline levels should be monitored closely in patients who use marijuana or its cannabinoids. Dosage adjustments may be required following initiation, discontinuation, or change of cannabis/cannabinoid use.', 'Metabolism', 'Omalizumab, Tezepelumab, Benralizumab, Caffeine, Reslizumab, Montelukast, Dyphylline, Mepolizumab, Roflumilast, Pentoxifylline, Bromotheophylline', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Rufinamide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55216/', '[1] "Product Information. Dronabinol (dronabinol)." Watson Pharmaceuticals, Parsippany, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Jusko WJ, Schentag JJ, Clark JH, Gardner M, Yurchak AM "Enhanced biotransformation of theophylline in marijuana and tobacco smok