INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55322, 'Capecitabine', 'Sorafenib', 'Moderate', 'Coadministration with sorafenib may increase the plasma concentrations of capecitabine and its active metabolite, 5-fluorouracil (5-FU). The mechanism of interaction has not been described. Sorafenib pharmacokinetics were not significantly altered by capecitabine.', NULL, 'Caution may be advisable when capecitabine is used with sorafenib.', NULL, 'Caution may be advisable when capecitabine is used with sorafenib. Patients should be monitored closely for potential toxicities of capecitabine such as diarrhea, hand and foot syndrome, hyperbilirubinemia, myelosuppression and cardiotoxicity, and the dosage adjusted accordingly.', 'Others', 'Entrectinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, More', 'Pemetrexed, Mercaptopurine, Cladribine, Trifluridine, Floxuridine, Nelarabine, Cytarabine, Pralatrexate, Fludarabine, Decitabine, Azacitidine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55393/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Nexavar (sorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.[3] "Product Information. Nexavar (sorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767369485);
