INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55344, 'Capecitabine', 'Vincristine', 'Minor', 'Coadministration of fluorouracil and vincristine may lead to an earlier onset and/or increased severity of side effects. The mechanism is inhibition of the metabolism of both drugs by a hepatic cytochrome P-450 isoenzyme in the CYP 3A subfamily. The clinical significance of this interaction has not been established. If fluorouracil and vincristine are used concomitantly, caution and close observation are advised.', NULL, '-', NULL, '-', 'Metabolism', 'Teniposide, Trabectedin, Cabazitaxel', 'Pemetrexed, Trifluridine, Floxuridine, Pralatrexate, Decitabine, Azacitidine', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55415/', '[1] Zhou-Pan XR, Seree E, Zhou XJ, et al "Involvement of human liver cytochrome P450 3A in vinblastine metabolism: drug interactions." Cancer Res 53 (1993): 5121-6[2] "Product Information. Oncovin (vincristine)." Lilly, Eli and Company, Indianapolis, IN.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55345, 'Capecitabine', 'Vinorelbine', 'Moderate', 'The concomitant or sequential administration of multiple antineoplastic agents may result in additive toxicities, particularly in the bone marrow and gastrointestinal tract.', NULL, 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals.', NULL, 'Close clinical and laboratory monitoring for hematologic and nonhematologic toxicities are recommended when antineoplastic agents are administered concurrently or during close intervals. Dosing adjustments may be necessary. The manufacturers'' recommendations and institutional protocols for dosage, treatment regimens, monitoring, and management of toxicities should be consulted.', 'Synergism', 'Teniposide, Trabectedin, Cabazitaxel', 'Azacitidine, Pralatrexate, Trifluridine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55416/', '[1] "Product Information. Fluorouracil injection (fluorouracil)." Roche Laboratories, Nutley, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] EMEA. European Medicines Agency "EPARs. European Union Public Assessment Reports. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/includes/medicines/medicines_landingpage.jsp&mid."[4] "Product Information. Paraplatin (carboplatin)." Bristol-Myers Squibb, Princeton, NJ.[5] Cerner Multum, Inc. "Australian Product Information." O 0[6] "Product Information. Ellence (epirubicin)" Pharmacia and Upjohn, Kalamazoo, MI.[7] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[8] "Product Information. Ifex (ifosfamide)." Bristol-Myers Squibb, Princeton, NJ.[9] "Product Information. Zanosar (streptozocin)." Pharmacia and Upjohn, Kalamazoo, MI.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55346, 'Capecitabine', 'Vitamin E', 'Moderate', 'The potential effects of vitamin E on cancer chemotherapy and radiation have not been established. Because vitamin E is an antioxidant, pharmacologic doses could theoretically interfere with radiation therapy and chemotherapeutic agents whose cytotoxic mechanism depends on generation of reactive oxygen species (ROS) that damage DNA and proteins, including but not limited to alkylating agents (e.g., busulfan, cyclophosphamide, melphalan), anthracyclines (e.g., doxorubicin, epirubicin), platinum coordination complexes (e.g., cisplatin, carboplatin), DNA topoisomerase inhibitors (e.g., etoposide, teniposide, irinotecan, topotecan), and photodynamic agents (e.g., porfimer). On the other hand, vitamin E may help reduce oxidative stress associated with more aggressive cancers and protect non-cancer cells from oxidative damage generated by cancer treatments, which can enhance patient tolerance and lessen the need for reducing dosage or duration of treatment.', NULL, 'Until more information is available, vitamin E supplementation should preferably be avoided in patients undergoing cancer chemotherapy or radiation treatment.', NULL, 'Until more information is available, vitamin E supplementation should preferably be avoided in patients undergoing cancer chemotherapy or radiation treatment. Clinicians should closely monitor antitumor response if vitamin E is used in these patients. No specific dosing of vitamin E has been established for uses other than to treat vitamin E deficiency. There has been evidence suggesting possible adverse health effects including increased risk of heart failure and mortality in association wi