INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55347, 'Capecitabine', 'Warfarin', 'Major', 'Fluorouracil and its prodrug capecitabine may significantly potentiate the hypoprothrombinemic effect of warfarin and other oral anticoagulants. The mechanism of the interaction is unknown. Altered coagulation parameters and/or bleeding have been reported in patients stabilized on a coumarin-derivative anticoagulant such as warfarin following the addition of fluorouracil or capecitabine.', NULL, 'Patients should be closely monitored during concomitant therapy.', NULL, 'Patients should be closely monitored during concomitant therapy. The INR or PT should be checked frequently and anticoagulant dosage adjusted accordingly, particularly following initiation or discontinuation of fluorouracil or capecitabine therapy in patients who are stabilized on their anticoagulant regimen. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', 'Ticagrelor, Betrixaban, Apixaban, Cilostazol, Rivaroxaban, Iloprost, Lepirudin, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, More', 'Cladribine, Clofarabine, Trifluridine, Floxuridine, Nelarabine, Pralatrexate, Decitabine, Azacitidine, Tioguanine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55418/', '[1] Brown MC "Multisite mucous membrane bleeding due to a possible interaction between warfarin and 5-flourouracil." Pharmacotherapy 17 (1997): 63