INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55541, 'Capmatinib', 'Lapatinib', 'Moderate', 'Coadministration with capmatinib may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters, such as digoxin and rosuvastatin. The proposed mechanism is decreased clearance due to capmatinib-mediated inhibition of intestinal P-gp and/or BCRP efflux transport proteins.', NULL, 'Coadministration of capmatinib with drugs that are substrates of P-gp and/or BCRP should generally be avoided.', NULL, 'Coadministration of capmatinib with drugs that are substrates of P-gp and/or BCRP should generally be avoided. However, if concomitant use is unavoidable, caution is advised, particularly with drugs that have a narrow therapeutic range. Clinical and laboratory monitoring should be considered whenever capmatinib is added to or withdrawn from therapy with these drugs, and dosages adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Absorption', 'Entrectinib, Asciminib, Acalabrutinib, Dacomitinib, Avapritinib, Selumetinib, Vandetanib, Capmatinib, Ibrutinib, Tivozanib, Zanubrutinib, More', 'Regorafenib, Bosutinib, Lapatinib, Trametinib, Dacomitinib, Alectinib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55612/', '[1] "Product Information. Tabrecta (capmatinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
