INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55598, 'Capmatinib', 'Ranolazine', 'Moderate', 'Coadministration with inhibitors of the P-glycoprotein (P-gp) efflux transporter may increase the oral bioavailability of ranolazine, which has been shown in vitro to be a substrate of P-gp. Ranolazine can prolong the QT interval in a dose-dependent manner, thus increased plasma levels may potentiate the risk of ventricular arrhythmias including ventricular fibrillation and torsade de pointes.', NULL, 'Caution is advised if ranolazine is prescribed in combination with P-gp inhibitors.', NULL, 'Caution is advised if ranolazine is prescribed in combination with P-gp inhibitors. Pharmacologic response to ranolazine should be monitored more closely whenever a P-gp inhibitor is added to or withdrawn from therapy, and the ranolazine dosage adjusted as necessary. Patients should be advised to seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope.', 'Absorption', 'Alprostadil, Indomethacin, Ibuprofen, Ubidecarenone, Ivabradine, Adenosine, Regadenoson', 'Regorafenib, Temsirolimus, Trametinib, Erlotinib, Neratinib, Tivozanib, Alectinib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55669/', '[1] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.[2] "Product Information. Ranexa (ranolazine)." Calmoseptine Inc, Huntington Beach, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55599, 'Capmatinib', 'Rasagiline', 'Major', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of rasagiline, which is primarily metabolized by the isoenzyme. Because the MAO-B selectivity of rasagiline may diminish with increasing dosage above the recommended range of 0.5 to 1 mg/day, there may be an increased risk of hypertensive crisis and other adverse reactions associated with nonselective inhibition of MAO if the maximum dosage is used in the presence of ciprofloxacin or other CYP450 1A2 inhibitors.', NULL, 'Rasagiline should be prescribed at a maximum dosage of 0.', NULL, 'Rasagiline should be prescribed at a maximum dosage of 0.5 mg/day during coadministration with moderate or potent CYP450 1A2 inhibitors. Patients should be advised to promptly seek medical attention if they experience potential signs and symptoms of a hypertensive crisis such as severe headache, visual disturbances, confusion, stupor or coma, seizures, chest pain, unexplained nausea or vomiting, and stroke-like symptoms.', 'Metabolism', 'Apomorphine, Opicapone, Cabergoline, Amantadine, Entacapone, Selegiline, Tolcapone, Rotigotine, Bromocriptine, Pergolide, Pramipexole, More', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pexidartinib, Tepotinib, Fedratinib, Avapritinib, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55670/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Azilect (rasagiline)." Teva Pharmaceuticals USA, North Wales, PA.[4] McGrath