INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55733, 'Capreomycin', 'Pipecuronium', 'Moderate', 'Like other polypeptide antibiotics, capreomycin may prolong apnea and respiratory paralysis after use of neuromuscular blocking agents. The mechanism may be related to decreased intracellular potassium or decreased ionized serum calcium.', NULL, 'Patients should be closely monitored for signs of excessive and/or extended neuromuscular blockade.', NULL, 'Patients should be closely monitored for signs of excessive and/or extended neuromuscular blockade.', 'Others', 'Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Rifapentine, Trimethoprim, Rifampicin, Cycloserine, Rifabutin, Isoniazid, Ethambutol, More', 'Botulinum toxin type A', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55804/', '[1] Kronenfeld MA, Thomas SJ, Turndorf H "Recurrence of neuromuscular blockade after reversal of vecuronium in a patient receiving polymyxin/amikacin sternal irrigation." Anesthesiology 65 (1986): 93-4[2] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online de medicamentos de la AEMPS - CIMA. Available from: URL: https://cima.aemps.es/cima/publico/home.html." ([2018]):[3] "Product Information. Capastat Sulfate (capreomycin)." Dura Pharmaceuticals, San Diego, CA.[4] Huang KC, Heise A, Shrader AK, Tsueda K "Vancomycin enhances the neuromuscular blockade of vecuronium." Anesth Analg 71 (1990): 194-6', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55734, 'Capreomycin', 'Plazomicin', 'Major', 'The combination of a polypeptide antibiotic and an aminoglycoside may increase the risk of neurotoxicity, respiratory paralysis, and renal dysfunction. The mechanism of this interaction is not known, but may involve additive or synergistic effects.', NULL, 'This combination should be avoided concurrently or sequentially.', NULL, 'This combination should be avoided concurrently or sequentially. If coadministration is medically necessary, patients should be closely monitored for development of toxicity, including renal function tests, respiratory function, and vestibular and audiometric tests.', 'Synergism', 'Pyrazinamide, Rifamycin, Bedaquiline, Rifapentine, Rifampicin, Cycloserine, Rifabutin, Isoniazid, Ethambutol, Ethionamide, Pyridoxine, More', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55805/', '[1] "Product Information. Garamycin (gentamicin)." Schering-Plough Corporation, Kenilworth, NJ.[2] "Product Information. Arikayce (amikacin liposome)." Insmed Incorporated, Bridgewater, NJ.[3] Lindesmith LA, Baines RD Jr, Bigelow DB, Petty TL "Reversible respiratory paralysis associated with polymyxin therapy." Ann Intern Med 68 (1968): 318-27[4] Lee C, de Silva AJ "Interaction of neuromuscular blocking effects of neomycin and polymyxin B." Anesthesiology 50 (1979): 218-20[5] "Product Information. Nebcin (tobramycin)." Lilly, Eli and Company, Indianapolis, IN.[6] "Product Information. Amikin (amikacin)." Bristol-Myers Squibb, Princeton, NJ.[7] Koch-Weser J, Sidel VW, Federman EB, Kanarek P, Finer DC, Eaton AE "Adverse effects of sodium colistimethate: manifestations and specific reaction rates during 317 courses of therapy." Ann Intern Med 72 (1970): 857-68', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55735, 'Capreomycin', 'Polymyxin B', 'Moderate', 'Increased adverse effects are possible when glycopeptide antibiotics are administered concomitantly with other potentially nephrotoxic and neurotoxic drugs.', NULL, 'If these drugs must be used together, renal function, auditory and vestibular function, and serum drug concentrations should be monitored.', NULL, 'If these drugs must be used together, renal function, auditory and vestibular function, and serum drug concentrations should be monitored.', 'Synergism', 'Pyrazinamide, Rifamycin, Bedaquiline, Rifapentine, Rifampicin, Cycloserine, Rifabutin, Isoniazid, Ethambutol, Ethionamide, Pyridoxine, More', 'Tetracycline, Rifamycin, Miconazole, Chloramphenicol, Chlorhexidine, Ofloxacin, Tetracycline, Chloramphenicol, Chlorhexidine, Tinidazole, Linezolid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55806/', '[1] Dubrovskaya Y, Prasad N, Lee Y, Esaian D, Figueroa DA, Tam VH "Risk factors for nephrotoxicity onset associated with polymyxin B therapy." J Antimicrob Chemother 70 (2015): 1903-7[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Vancocin (vancomycin)." Lilly, Eli and Company, Indianapolis, IN.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55736, 'Capreomycin', 'Prednisolone', 'Moderate', 'The concomitant use of corticosteroids and agents that deplete potassium (e.g., potassium-wasting diuretics, amphotericin B, cation exchange resins) may result in increased risk of hypokalemia. Corticosteroids can produce hypokalemia and other electrolyte disturbances via mineralocorticoid effects, the degree of which varies with the agent and route of administration (i.e. systemic vs. local).', NULL, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given.', NULL, 'Patients receiving potassium-depleting agents with corticosteroids should be monitored closely for development of hypokalemia, particularly if fludrocortisone or large doses of another corticosteroid or adrenocorticotropic agent is given. Potassium supplementation may be necessary. Patients should be advised to notify their physician if they experience signs of electrolyte disturbances such as weakness, lethargy, and muscle pains or cramps.', 'Synergism', 'Rifamycin, Bedaquiline, Sulfamethoxazole, Cycloserine, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Mometasone furoate, Amcinonide, Fluocinolone acetonide, Hydrocortisone butyrate, Fluticasone, Fluocinonide, Fluorometholone, Clobetasol, Fluorometholone, Indomethacin, Fluocinolone acetonide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55807/', '[1] Morris GC, Egan JG, Jones MK "Hypokalaemic paralysis induced by bolus prednisolone in Graves'' disease." Aust N Z J Med 22 (1992): 312[2] Thomas TP "The complications of systemic corticosteroid therapy in the elderly." Gerontology 30 (1984): 60-5[3] "Product Information. Hydeltrasol (prednisolone)." Merck & Co, Inc, West Point, PA.[4] Ramsahoye BH, Davies SV, el-Gaylani N, Sandeman D, Scanlon MF "The mineralocorticoid effects of high dose hydrocortisone." BMJ 310 (1995): 656-7[5] Powell JR "Steroid and hypokalemic myopathy after corticosteroids for ulcerative colitis. Systemic and tropical application." Am J Gastroenterol 52 (1969): 425-32[6] Thorn GW "Clinical considerations in the use of corticosteroids." N Engl J Med 274 (1966): 775-81[7] Seale JP, Compton MR "Side-effects of corticosteroid agents." Med J Aust 144 (1986): 139-42[8] Chrousos GA, Kattah JC, Beck RW, Cleary PA "Side effects of glucocorticoid treatment. Experience of the Optic Neuritis Treatment Trial." JAMA 269 (1993): 2110-2', 1767369485);
