INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55764, 'Captopril', 'Aurothioglucose', 'Moderate', 'Concomitant ACE inhibitor therapy may potentiate the risk of nitritoid (vasomotor) reactions associated with injectable gold therapy. The mechanism of interaction is unknown, although investigators suggest that ACE inhibitors may unmask drug hypersensitivity reactions by preventing the breakdown of bradykinins.', NULL, 'Patients on injectable gold therapy in combination with an ACE inhibitor should be monitored for symptoms of nitritoid reactions such as facial flushing, nausea, vomiting, and hypotension following an injection.', NULL, 'Patients on injectable gold therapy in combination with an ACE inhibitor should be monitored for symptoms of nitritoid reactions such as facial flushing, nausea, vomiting, and hypotension following an injection. Elderly patients in particular should be observed following treatment. Oxygen, intravenous fluids, epinephrine, steroids, and other supportive therapy may be necessary for treatment.', 'Others', 'Felodipine, Amlodipine, Verapamil, Bisoprolol, Hydrochlorothiazide, Indapamide', 'Auranofin, Penicillamine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55835/', '[1] "Product Information. Prinivil (lisinopril)." Merck & Co, Inc, West Point, PA.[2] Nixon J, Pande I "Gold, nitritoid reactions and angiotensin-converting enzyme inhibitors." Rheumatology (Oxford) 45 (2006): 118-9[3] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[4] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[5] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55765, 'Captopril', 'Bedaquiline', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', 'Felodipine, Amlodipine, Verapamil, Bisoprolol, Hydrochlorothiazide, Indapamide', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Rifapentine, Streptomycin, Cycloserine, Rifabutin, Isoniazid, Ethambutol, Ethionamide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55836/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[3] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767369485);
