INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28025, 'Ponatinib', 'Apixaban', 'Major', 'Coadministration of ponatinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with ponatinib is associated with severe, sometimes fatal hemorrhage including cerebral and gastrointestinal hemorrhage.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ponatinib.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with ponatinib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. The INR should be monitored more frequently during coadministration of warfarin. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28078/', '[1] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.[3] "Product Information. Iclusig (ponatinib)." Ariad Pharmaceuticals Inc, Cambridge, MA.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28026, 'Posaconazole', 'Apixaban', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 that can also inhibit P-glycoprotein (P-gp) may significantly increase the plasma concentrations of apixaban, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'The use of apixaban should either be avoided or the dosage reduced during concomitant systemic treatment with dual P-gp and potent CYP450 3A4 inhibitors.', NULL, 'The use of apixaban should either be avoided or the dosage reduced during concomitant systemic treatment with dual P-gp and potent CYP450 3A4 inhibitors. In addition, some authorities recommend avoiding concomitant use of apixaban during and for 2 weeks after treatment with itraconazole. For patients receiving apixaban 5 mg or 10 mg twice daily, the prescribing information recommends reducing the dosage by 50%. In patients already taking apixaban at a dosage of 2.5 mg twice daily (e,g., patients meeting at least 2 of the following criteria: age >=80 years; body weight <=60 kg; serum creatinine >=1.5 mg/dL), coadministration with dual P-gp and potent CYP450 3A4 inhibitors should be avoided.', 'Metabolism, Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28079/', '[1] "Product Information. Eliquis (apixaban)." Bristol-Myers Squibb Canada Inc, Montreal, IN.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations f