INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28082, 'Telaprevir', 'Apixaban', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 that can also inhibit P-glycoprotein (P-gp) may significantly increase the plasma concentrations of apixaban, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'The use of apixaban should either be avoided or the dosage reduced during concomitant systemic treatment with dual P-gp and potent CYP450 3A4 inhibitors.', NULL, 'The use of apixaban should either be avoided or the dosage reduced during concomitant systemic treatment with dual P-gp and potent CYP450 3A4 inhibitors. In addition, some authorities recommend avoiding concomitant use of apixaban during and for 2 weeks after treatment with itraconazole. For patients receiving apixaban 5 mg or 10 mg twice daily, the prescribing information recommends reducing the dosage by 50%. In patients already taking apixaban at a dosage of 2.5 mg twice daily (e,g., patients meeting at least 2 of the following criteria: age >=80 years; body weight <=60 kg; serum creatinine >=1.5 mg/dL), coadministration with dual P-gp and potent CYP450 3A4 inhibitors should be avoided.', 'Metabolism, Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28135/', '[1] "Product Information. Eliquis (apixaban)." Bristol-Myers Squibb Canada Inc, Montreal, IN.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for