INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28122, 'Bupropion', 'Apomorphine', 'Moderate', 'Central nervous system (CNS) depressant effects may be additively or synergistically increased in patients using apomorphine in combination with other drugs that can also cause these effects. Apomorphine alone has been frequently associated with somnolence and dizziness. Patients may suddenly fall asleep during activities of daily living.', NULL, 'The use of other sedating drugs should generally be avoided during apomorphine treatment.', NULL, 'The use of other sedating drugs should generally be avoided during apomorphine treatment. Patients prescribed these agents concurrently should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28175/', '[1] "Product Information. Apokyn (apomorphine)." Mylan Pharmaceuticals Inc, Morgantown, WV.[2] "Product Information. Zohydro ER (HYDROcodone)." Zogenix, Inc, San Diego, CA.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28123, 'Bupropion', 'Aripiprazole', 'Major', 'The use of bupropion is associated with a dose-related risk of seizures. The risk may be further increased when coadministered with other agents that can reduce the seizure threshold, including antidepressants, CNS stimulants, acetylcholinesterase inhibitors, phenothiazines, and dopaminergic blocking agents such as neuroleptics and metoclopramide. Coadministration with bupropion may increase the plasma concentrations of drugs that are metabolized by CYP450 2D6, including many antidepressants, neuroleptics, CNS stimulants (e.g., amphetamines), metoclopramide, and some acetylcholinesterase inhibitors (e.g., donepezil, galantamine). The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by bupropion and its metabolite, hydroxybupropion.', NULL, 'Extreme caution is advised if bupropion is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with a history of seizures or other risk factors for seizures (e.', NULL, 'Extreme caution is advised if bupropion is administered with any substance that can reduce the seizure threshold, particularly in the elderly and in patients with a history of seizures or other risk factors for seizures (e.g., head trauma; brain tumor; severe hepatic cirrhosis; metabolic disorders; CNS infections; excessive use of alcohol or sedatives; addiction to opiates, cocaine, or stimulants; diabetes treated with oral hypoglycemic agents or insulin). Bupropion as well as concomitant medications should be initiated at the lower end of the dosage range and titrated gradually as needed and as tolerated. The maximum recommended dosage for the specific bupropion formulation should not be exceeded.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28176/', '[1] Johnston JA, Lineberry CG, Ascher JA, et al. "A 102-center prospective study of seizure in association with bupropion." J Clin Psychiatry 52 (1991): 450-6[2] Shin YW, Erm TM, Choi EJ, Kim SY "A Case of Prolonged Seizure Activity After Combined Use of Bupropion and Clomipramine." Clin Neuropharmacol 27 (2004): 192-194[3] James WA, Lippmann S "Bupropion: overview and prescribing guidelines in depression." South Med J 84 (1991): 222-4[4] Sheehan DV, Welch JB, Fishman SM "A case of bupropion-induced seizure." J Nerv Ment Dis 174 (1986): 496-8[5] Storrow AB "Bupropion overdose and seizure." Am J Emerg Med 12 (1994): 183-4[6] Shad MU "A possible bupropion and imipramine interaction." J Clin Psychopharmacol 17 (1997): 118[7] Masco HL, Kiev A, Holloman LC, Batey SR, Johnston JA, Lineberry CG "Safety and efficacy of bupropion and nortriptyline in outpatients with depression." Curr Ther Res Clin Exp 55 (1994): 851-63[8] "Product Information. Wellbutrin (bupropion)." Glaxo Wellcome, Research Triangle Park, NC.[9] Dufresne RL, Weber SS, Becker RE "Bupropion hydrochloride." Drug Intell Clin Pharm 18 (1984): 957-64[10] "Product Information. Zyban (bupropion)." Glaxo Wellcome, Research Triangle Park, NC.[11] Gittelman DK, Kirby MG "A seizure following bupropion overdose." J Clin Psychiatry 54 (1993): 162[12] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[13] Enns MW "Seizure during combination of trimipramine and bupropion." J Clin Psychiatry 62 (2001): 476-7[14] Rosenstein DL, Nelson JC, Jacobs SC "Seizures associated with antidepressants: a review." J Clin Psychiatry 54 (1993): 289-99[15] Guzey C, Norstrom A, Spigset O "Change from the CYP2D6 extensive metabolizer to the poor met