INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28331, 'Bupropion', 'Metoprolol', 'Moderate', 'Coadministration with bupropion may increase the plasma concentrations of drugs that are metabolized by CYP450 2D6 (e.g., selective serotonin reuptake inhibitors; tricyclic antidepressants; some beta blockers, antiarrhythmics, and antipsychotics). The mechanism is decreased clearance due to inhibition of CYP450 2D6 activity by bupropion.', NULL, 'Caution is advised if bupropion must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if bupropion must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Concomitant medications should be initiated at the lower end of the dose range. Clinical and laboratory monitoring may be appropriate for some drugs whenever bupropion is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28384/', '[1] "Product Information. Wellbutrin XL (buPROPion)." GlaxoSmithKline, Philadelphia, PA.[2] "Product Information. Wellbutrin SR (bupropion)." Glaxo Wellcome, Research Triangle Park, NC.[3] James WA, Lippmann S "Bupropion: overview and prescribing guidelines in depression." South Med J 84 (1991): 222-4[4] Enns MW "Seizure during combination of trimipramine and bupropion." J Clin Psychiatry 62 (2001): 476-7[5] Shad MU "A possible bupropion and imipramine interaction." J Clin Psychopharmacol 17 (1997): 118[6] Dufresne RL, Weber SS, Becker RE "Bupropion hydrochloride." Drug Intell Clin Pharm 18 (1984): 9