INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28499, 'Apraclonidine', 'Apomorphine', 'Moderate', 'Topically administered alpha-2 adrenergic receptor agonists such as apraclonidine and brimonidine are systemically absorbed, with the potential for producing rare but clinically significant systemic effects. Although the interaction has not been specifically studied, the possibility of an additive or potentiating effect with central nervous system (CNS) depressants such as alcohol, barbiturates, opiates, anxiolytics, sedatives, and anesthetics should be considered.', NULL, 'Patients receiving topical alpha-2 adrenergic receptor agonists in combination with agents that can cause CNS depression should be made aware of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness and confusion, and counseled to avoid activities requiring mental alertness.', NULL, 'Patients receiving topical alpha-2 adrenergic receptor agonists in combination with agents that can cause CNS depression should be made aware of the potential for increased adverse effects such as drowsiness, dizziness, lightheadedness and confusion, and counseled to avoid activities requiring mental alertness. Patients should also avoid rising abruptly from a sitting or recumbent position and notify their physician if they experience orthostasis or tachycardia.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28552/', '[1] "Product Information. Mirvaso (brimonidine topical)." Galderma Laboratories Inc, Cranbury, NJ.[2] "Product Information. Alphagan (brimonidine ophthalmic)." Allergan Inc, Irvine, CA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Iopidine (apraclonidine)." Alcon Laboratories Inc, Fort Worth, TX.[5] Pekdemir M, Yanturali S, Karakus G "More than just an ocular solution." Emerg Med J 22 (2005): 753-4[6] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[7] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[8] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28500, 'Arformoterol', 'Apomorphine', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', NULL, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', NULL, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28553/', '[1] "Product Information. Arcapta Neohaler (indacaterol)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Whyte KF, Addis GJ, Whitesmith R, Reid JL "The mechanism of salbutamol-induced hypokalaemia." Br J Clin Pharmacol 23 (1987): 65-71[3] Sanders JP, Potter DE, Ellis S, Bee DE, Grant JA "Metabolic and cardiovascular effects of carbuterol and metaproterenol." J Allergy Clin Immunol 60 (1977): 174-