INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1340519, 'Aliskiren', 'Larotrectinib', 'Moderate', 'Coadministration with larotrectinib may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever larotrectinib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects. Some authorities recommend avoiding concomitant use of larotrectinib and sensitive CYP450 3A4 substrates or substrates with narrow therapeutic ranges. These drugs include, but are not limited to: alfentanil, cisapride, colchicine, cyclosporine, fentanyl, ivacaftor, lovastatin, simvastatin, oral midazolam, triazolam, pimozide, quinidine, sildenafil, ergot derivatives, macrolide immunosuppressants, and vinca alkaloids.', 'Metabolism', 'Caution is advised when larotrectinib is used concomitantly with drugs that are substrates of CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1340520, 'Aliskiren', 'Lasmiditan', 'Moderate', 'Coadministration with lasmiditan may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters. The proposed mechanism, based on in vitro data, involves decreased clearance due to inhibition of P-gp and BCRP by lasmiditan.', 'DDInter', 1767317475, 'Concomitant use of lasmiditan with drugs that are P-gp and/or BCRP substrates should be avoided when possible. If coadministration is required, patients should be carefully monitored for increased adverse reactions of these drugs and dosing adjusted as necessary.', 'Distribution', 'Concomitant use of lasmiditan with drugs that are P-gp and/or BCRP substrates should be avoided when possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1340521, 'Aliskiren', 'Lefamulin', 'Moderate', 'When administered orally, lefamulin may increase the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4. Based on interaction with midazolam, a sensitive CYP450 3A4 substrate, lefamulin may be a moderate CYP450 3A4 inhibitor. When oral midazolam was administered concomitantly with and at 2 or 4 hours after administration of lefamulin tablets, mean midazolam peak plasma concentration (Cmax) and systemic exposure (AUC) increased by approximately 100% and 200%, respectively.', 'DDInter', 1767317475, 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.g., ergot alkaloids, colchicine, fentanyl, macrolide immunosuppressants, midazolam, triazolam, vardenafil, vinca alkaloids). Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever oral lefamulin is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when oral lefamulin is used with drugs that undergo metabolism via CYP450 3A4, particularly sensitive CYP450 3A4 substrates or those that demonstrate a narrow therapeutic index (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
