INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1340541, 'Aliskiren', 'Nefazodone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and P-glycoprotein may increase the plasma concentrations and pharmacologic effects of aliskiren, which is a substrate of both the isoenzyme and efflux transporter. According to the product labeling, plasma levels of aliskiren were increased approximately 80% by the potent CYP450 3A4 and moderate P-gp inhibitor ketoconazole at a dosage of 200 mg twice daily.', 'DDInter', 1767317475, 'Pharmacologic response to aliskiren should be monitored more closely whenever a CYP450 3A4 and/or P-gp inhibitor is added to or withdrawn from therapy, and the aliskiren dosage adjusted if necessary. Patients should be advised to notify their physician if they experience excessive adverse effects of aliskiren such as dizziness, lightheadedness, diarrhea, abdominal pain, and gastroesophageal reflux.', 'Metabolism', 'Pharmacologic response to aliskiren should be monitored more closely whenever a CYP450 3A4 and/or P-gp inhibitor is added to or withdrawn from therapy, and the aliskiren dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
