INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1340778, 'Hydrochlorothiazide', 'Glycerin', 'Moderate', 'The chronic use or abuse of laxatives may potentiate the pharmacologic effects of diuretics. Laxatives can cause significant losses of fluid and electrolytes, including sodium, potassium, magnesium and zinc, and these effects may be additive to those of diuretics.', 'DDInter', 1767317475, 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages. During concomitant use with diuretics, patients should be advised to contact their physician if they experience signs and symptoms of fluid and electrolyte depletion such as dizziness, lightheadedness, dry mouth, thirst, fatigue, weakness, lethargy, muscle cramps, decreased urination, postural hypotension, and tachycardia. If maintenance of bowel regularity is required, patients should be advised to exercise and increase fiber in the diet and/or consider the use of bulk-forming laxatives.', 'Synergism', 'In general, laxatives should only be used on a short-term, intermittent basis in recommended dosages.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1340779, 'Hydrochlorothiazide', 'Glycopyrronium', 'Minor', 'Anticholinergic agents may increase the absorption and oral bioavailability of thiazide diuretics. The proposed mechanism involves increased gastrointestinal transit time due to reduction of stomach and intestinal motility by anticholinergic agents.', 'DDInter', 1767317475, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1340780, 'Hydrochlorothiazide', 'Guanabenz', 'Minor', 'The bioavailability of hydrochlorothiazide is greater when it is given with guanabenz than when it is given alone. The mechanism is not known, but it is suspected that guanabenz increases the gastrointestinal transit time of hydrochlorothiazide. No clinical intervention appears to be necessary.', 'DDInter', 1767317475, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
