INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56007, 'Captopril', 'Temsirolimus', 'Moderate', 'The use of mTOR (mammalian target of rapamycin) inhibitors has been associated with the development of angioedema, and coadministration with other drugs that are also known to cause angioedema such as ACE inhibitors, angiotensin II receptor antagonists, and renin inhibitors may increase the risk.', NULL, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when mTOR inhibitors are prescribed with other drugs that are also known to cause angioedema.', NULL, 'Clinicians and patients should be aware of the potential for increased risk of angioedema when mTOR inhibitors are prescribed with other drugs that are also known to cause angioedema. Patients should be advised to promptly discontinue these medications and seek medical attention if they develop signs or symptoms suggestive of angioedema such as swelling of the face, extremities, eyes, lips, or tongue, and difficulty swallowing or breathing.', 'Synergism', 'Hydrochlorothiazide, Indapamide, Bisoprolol', 'Entrectinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Dacomitinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, Larotrectinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56079/', '[1] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[5] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[6] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538[7] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
