INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56184, 'Clozapine', 'Carbamazepine', 'Major', 'The use of clozapine with other potentially myelotoxic agents such as carbamazepine may increase the risk and/or severity of hematologic toxicity. Clozapine alone is associated with a significant risk of agranulocytosis, defined as an absolute neutrophil count (ANC) of less than 500/mm3. Case reports also suggest that carbamazepine may decrease the pharmacologic effects of clozapine. The mechanism may involve induction of CYP450 hepatic metabolism by carbamazepine. Increased serum clozapine levels have been reported after discontinuation of carbamazepine therapy.', NULL, 'The use of clozapine in combination with other agents having a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function is considered contraindicated.', NULL, 'The use of clozapine in combination with other agents having a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function is considered contraindicated.', 'Metabolism, Synergism', 'Amisulpride', 'Lacosamide', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56256/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Jerling M, Lindstrom L, Bondesson U, Bertilsson L "Fluvoxamine inhibition and carbamazepine induction of the metabolism of clozapine - evidence from a therapeutic drug monitoring service." Ther Drug Monit 16 (1994): 368-74[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.ph