INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56185, 'Cobimetinib', 'Carbamazepine', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cobimetinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of cobimetinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of cobimetinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Brivaracetam, Topiramate, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Clonazepam, Paramethadione, Perampanel, More', 'Alectinib, Lenvatinib, Trametinib, Dacomitinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56257/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[5] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[7] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[8] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56186, 'Codeine', 'Carbamazepine', 'Moderate', 'Coadministration of codeine with potent or moderate CYP450 3A4 inducers may result in lower codeine plasma concentrations, higher levels of the inactive metabolite norcodeine, and less metabolism via CYP450 2D6, resulting in lower morphine levels. This interaction may lead to reduced codeine efficacy and potentially initiate the onset of withdrawal symptoms in patients who are physically dependent. In addition, patients may be at an increased risk of CNS and/or respiratory-depressant effects from increased levels of codeine once concomitant therapy with the CYP450 3A4 inducer is ceased.', NULL, 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer.', NULL, 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is considered necessary, caution and close clinical and laboratory monitoring are recommended. Dosage adjustments may also be required whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. Following discontinuation of a CYP450 3A4 inducer, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Metabolism', 'Naloxone, Benzonatate', 'Lacosamide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56258/', '[1]