INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56239, 'Dronedarone', 'Carbamazepine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of dronedarone, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of dronedarone with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, 'Concomitant use of dronedarone with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort should generally be avoided. The extent to which other, less potent CYP450 3A4 inducers may interact with dronedarone is unknown. Caution is advised if they are used with dronedarone.', 'Metabolism', 'Ethotoin, Brivaracetam, Gabapentin, Clonazepam, Valproic acid, Topiramate, Paramethadione, Mephenytoin, Perampanel, Methsuximide, Phensuximide, More', 'Propafenone, Tocainide, Procainamide, Flecainide, Disopyramide, Bretylium, Moricizine, Ibutilide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56311/', '[1] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[2] "Product Information. Multaq (dronedarone)." sanofi-aventis , Bridgewater, NJ.[3] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[4] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." Acta Med Scand 656 (1981