INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56270, 'Esterified estrogens', 'Carbamazepine', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', NULL, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Brivaracetam, Topiramate, Ezogabine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Rufinamide, Clonazepam, Paramethadione, More', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56342/', '[1] Back DJ, Breckenridge AM, Crawford FE, MacIver M, Orne ML, Rowe PH "Interindividual variation and drug interactions with hormonal steroid contraceptives." Drugs 21 (1981): 46-61[2] van Dijke CP, Weber JC "Interaction between oral contraceptives and griseofulvin." Br Med J (Clin Res Ed) 288 (1984): 1125-6[3]