INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56328, 'Isoniazid', 'Carbamazepine', 'Moderate', 'Isoniazid increases serum carbamazepine levels in most patients. Also, carbamazepine may potentiate isoniazid hepatotoxicity. The mechanism is CYP450 3A4 inhibition by isoniazid.', NULL, 'Close attention should be paid to clinical and laboratory evidence of hepatotoxicity and carbamazepine toxicity if patients must take isoniazid concurrently.', NULL, 'Close attention should be paid to clinical and laboratory evidence of hepatotoxicity and carbamazepine toxicity if patients must take isoniazid concurrently. Clinical observation of the patient is recommended, and carbamazepine dosage should be adjusted as indicated. Isoniazid should be discontinued if hepatotoxicity occurs. Patients should be advised to report signs of carbamazepine toxicity (nausea, visual disturbances, dizziness, or ataxia) or early signs of hepatotoxicity (fatigue, weakness, malaise, anorexia, nausea, or vomiting) to their caregiver.', 'Metabolism', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Tiagabine, Oxcarbazepine, Rufinamide, More', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, Aminosalicylic acid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56400/', '[1] Wright JM, Stokes EF, Sweeney VP "Isoniazid-induced carbamazepine toxicity and vice versa." N Engl J Med 307 (1982): 1325-7[2] Garcia B, Zaborras E, Areas V, et al "Interaction between isoniazid and carbamazepine potentiated by cimetidine." Ann Pharmacother 26 (1992): 841-2[3] Block SH "Carbamaz