INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56331, 'Itraconazole', 'Carbamazepine', 'Moderate', 'The coadministration with carbamazepine may result in decreased plasma concentrations of the azole antifungal agents, itraconazole and ketoconazole. The mechanism is accelerated azole clearance due to induction of the CYP450 3A4 enzymatic pathway by carbamazepine.', NULL, 'Because of these mutual effects, concomitant use of itraconazole or ketoconazole with carbamazepine is not recommended.', NULL, 'Because of these mutual effects, concomitant use of itraconazole or ketoconazole with carbamazepine is not recommended. In addition, some authorities recommend avoiding concomitant therapy from 2 weeks before, during, and for 2 weeks after treatment with itraconazole. Patients should be monitored for carbamazepine toxicity and antifungal treatment failure and dose adjustments may be required. Alternatively, other antifungal agents which are less likely to interact may be considered (e.g., terbinafine or fluconazole). Patients should be advised to report signs of carbamazepine toxicity (nausea, visual disturbances, dizziness, or ataxia) to their physicians.', 'Metabolism', 'Brivaracetam, Topiramate, Ezogabine, Paramethadione, Methsuximide, Fenfluramine, Eslicarbazepine, Lamotrigine, Phensuximide, Methylphenobarbital, Phenacemide, More', 'Flucytosine, Anidulafungin, Micafungin, Oteseconazole', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56403/', '[1] Spina E, Arena D, Scordo MG, Fazio A, Pisani F, Perucca E "Elevation of plasma carbamazepine concentrations by ketoconazole in patients with epilepsy." Ther Drug Monit 19 (1997): 535-8[2] Schaferkorting M "Pharmacok