INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56410, 'Carbamazepine', 'Mifepristone', 'Moderate', 'Coadministration of mifepristone with a drug that is both a substrate as well as inducer of CYP450 3A4 may result in decreased plasma concentrations of mifepristone and increased plasma concentrations of the other drug. Mifepristone itself is a substrate and inhibitor of CYP450 3A4. Theoretically, mifepristone may inhibit metabolism of the coadministered drug while its own metabolism may be induced by the coadministered drug.', NULL, 'When a drug that is both a substrate as well as inducer of CYP450 3A4 is coadministered with mifepristone, the possibility of diminished therapeutic response to mifepristone should be considered.', NULL, 'When a drug that is both a substrate as well as inducer of CYP450 3A4 is coadministered with mifepristone, the possibility of diminished therapeutic response to mifepristone should be considered. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism', 'Ethotoin, Brivaracetam, Gabapentin, Valproic acid, Topiramate, Paramethadione, Methsuximide, Perampanel, Fenfluramine, Eslicarbazepine, Lamotrigine, More', 'Prasterone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56482/', '[1] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.[2] He K, Woolf TF, Hollenberg PF "Mechanism-based inactivation of cytochrome P-450-3A4 by mifepristone (RU486)." J Pharmacol Exp Ther 288 (1999): 791-7[3] "Product Information. Di