INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56423, 'Carbamazepine', 'Naltrexone', 'Moderate', 'Coadministration of naltrexone with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Naltrexone, especially in larger than recommended doses (more than 50 mg/day), has been associated with hepatocellular injury, hepatitis, and elevations in liver transaminases and bilirubin. Other potential causative or contributory etiologies identified include preexisting alcoholic liver disease, hepatitis B and/or C infection, and concomitant usage of other hepatotoxic drugs.', NULL, 'The use of naltrexone with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of naltrexone with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Periodic monitoring of hepatic function is advisable.', 'Synergism', 'Brivaracetam, Topiramate, Ezogabine, Cenobamate, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Rufinamide, Paramethadione, More', 'Naloxone, Nalmefene, Disulfiram, Acamprosate, Dexfenfluramine, Ephedrine, Phentermine, Lorcaserin, Diethylpropion, Mazindol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56495/', '[1] "Product Information. ReVia (naltrexone)." DuPont Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56424, 'Carbamazepine', 'Nefazodone', 'Major', 'Coadministration of carbamazepine and nefazodone may result in significantly decreased plasma concentrations of nefazodone and its active metabolite, hydroxynefazodone, and increased plasma concentrations of carbamazepine. The proposed mechanism is carbamazepine induction of nefazodone and hydroxynefazodone metabolism via CYP450 3A4, and nefazodone inhibition of carbamazepine metabolism via the same isoenzyme.', NULL, 'Concomitant use of nefazodone and carbamazepine is considered contraindicated.', NULL, 'Concomitant use of nefazodone and carbamazepine is considered contraindicated.', 'Metabolism', 'Fosphenytoin, Phenytoin', 'Oxitriptan, Tryptophan, Milnacipran, Levomilnacipran', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56496/', '[1] Yong JN "Nefazodone and neurotoxicity." J Clin Psychiatry 58 (1997): 365[2] "Product Information. Serzone (nefazodone)." Bristol-Myers Squibb, Princeton, NJ.[3] Nemeroff CB, Devane CL, Pollock BG "Newer antidepressants and the cytochrome p450 system." Am J Psychiatry 153 (1996): 311-20[4] Laroudie C, Salazar DE, Cosson JP, Cheuvart B, Istin B, Girault J, Ingrand I, Decourt JP "Carbamazepine-nefazodone interaction in healthy subjects." J Clin Psychopharmacol 20 (2000): 46-53[5] Roth L, Bertschy G "Nefazodone may inhibit the metabolism of carbamazepine: three case reports." Eur Psychiatry 16 (2001): 320-1[6] Ashton AK, Wolin RE "Nefazodone-induced carbamazepine toxicity." Am J Psychiatry 153 (1996): 733[7] Garg SK, Kumar N, Bhargava VK, Prabhakar SK "Effect of grapefruit juice on carbamazepine bioavailability in patients with epilepsy." Clin Pharmacol Ther 64 (1998): 286-8[8] "Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals, East Hanover, NJ.[9] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[10] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[11] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[12] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[13] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56425, 'Carbamazepine', 'Nelfinavir', 'Moderate', 'Coadministration of nelfinavir and carbamazepine may result in decreased plasma concentrations of nelfinavir and increased plasma concentrations of carbamazepine. The proposed mechanism involves carbamazepine induction of nelfinavir metabolism via CYP450 3A4 and, conversely, nelfinavir inhibition of carbamazepine metabolism via the same enzymatic pathway.', NULL, 'Given the lack of pharmacokinetic data and the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, it may be preferable to avoid using nelfinavir in combination with carbamazepine.', NULL, 'Given the lack of pharmacokinetic data and the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, it may be preferable to avoid using nelfinavir in combination with carbamazepine. Caution is advised if concomitant use is required. In patients already receiving nelfinavir as part of their HIV treatment regimen, close clinical and laboratory monitoring of antiretroviral response is recommended following the addition or discontinuation of carbamazepine. Likewise, if nelfinavir is added to stable carbamazepine therapy, serum drug levels and pharmacologic effects should be closely monitored and the carbamazepine dosage adjusted accordingly. Patients should be advised to contact their doctor if they develop signs of carbamazepine toxicity such as ataxia, disorientation, dizziness, nausea, vomiting, and visual disturbances.', 'Metabolism', 'Paritaprevir, Zanamivir, Velpatasvir, Brincidofovir, Elbasvir, Famciclovir, Voxilaprevir, Pibrentasvir, Rimantadine, Telbivudine, Entecavir, More', 'Ethotoin, Brivaracetam, Rufinamide, Gabapentin, Topiramate, Ezogabine, Paramethadione, Mephenytoin, Methsuximide, Fenfluramine, Lamotrigine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56497/', '[1] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Bates DE, Herman RJ "Carbamazepine toxicity induced by lopinavir/ritonavir and nelfinavir." Ann Pharmacother 40 (2006): 1190-5[4] Brooks J, Daily J, Schwamm L "Protease inhibitors and anticonvulsants." AIDS Clin Care 9 (1997): 87,90[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Garg SK, Kumar N, Bhargava VK, Prabhakar SK "Effect of grapefruit juice on carbamazepine bioavailability in patients with epilepsy." Clin Pharmacol Ther 64 (1998): 286-8[7] "Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals, East Hanover, NJ.[8] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56426, 'Carbamazepine', 'Neratinib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of neratinib, which is primarily metabolized by the isoenzyme.', NULL, 'Given the potential for reduced efficacy of neratinib, concomitant use with potent or moderate inducers of CYP450 3A4 should generally be avoided.', NULL, 'Given the potential for reduced efficacy of neratinib, concomitant use with potent or moderate inducers of CYP450 3A4 should generally be avoided. According to some authorities, if the CYP450 3A4 inducer cannot be avoided, the dose of neratinib should be increased to 320 mg once daily. The daily dose of neratinib should not exceed 320 mg. The previous dose of neratinib may be resumed following discontinuation of the CYP450 3A4 inducer.', 'Metabolism', 'Brivaracetam, Topiramate, Ezogabine, Lamotrigine, Methylphenobarbital, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Oxcarbazepine, Ethotoin, More', 'Alectinib, Lenvatinib, Trametinib, Dacomitinib', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56498/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Abbas R, Hug BA, Leister C, Burns J, Sonnichsen D "Pharmacokinetics of oral neratinib during co-administration of ketoconazole in healthy subjects." Br J Clin Pharmacol 71 (2011): 522-7[5] "Product Information. Nerlynx (neratinib)." Puma Biotechnology, Inc., Los Angeles, CA.[6] Patel S, Robinson R, Burk M "Hyper