INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56427, 'Carbamazepine', 'Nevirapine', 'Moderate', 'Coadministration of carbamazepine and nevirapine may result in decreased plasma concentrations of both drugs. Since both are substrates as well as inducers of CYP450 3A4, it is conceivable that each may increase metabolism of the other. However, data are limited. However, the effect of the interaction on nevirapine during prolonged administration as part of highly active antiretroviral therapy (HAART) is unknown. In addition, the effect of nevirapine on carbamazepine pharmacokinetics has not been reported.', NULL, 'Caution is advised during coadministration of carbamazepine and nevirapine.', NULL, 'Caution is advised during coadministration of carbamazepine and nevirapine. Pharmacologic effects and serum drug concentrations should be monitored more closely whenever either drug is added to or withdrawn from therapy.', 'Metabolism', 'Paritaprevir, Zanamivir, Velpatasvir, Brincidofovir, Elbasvir, Famciclovir, Voxilaprevir, Pibrentasvir, Rimantadine, Telbivudine, Entecavir, More', 'Brivaracetam, Topiramate, Ezogabine, Methylphenobarbital, Lacosamide, Levetiracetam, Ethotoin, Rufinamide, Paramethadione, Fosphenytoin, Vigabatrin, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56499/', '[1] "Product Information. Viramune (nevirapine)." Boehringer-Ingelheim, Ridgefield, CT.[2] "Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals, East Hanover, NJ.[3] L''homme RF, Dijkema T, van der Ven AJ, Burger DM "Enzyme inducers reduce elimination half-life after a single dose of nevirapine in healthy women." J Acquir Immune Defic Syndr 43 (2006): 193-6