INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56438, 'Carbamazepine', 'Nortriptyline', 'Moderate', 'Carbamazepine may decrease serum concentrations of tricyclic antidepressants. The mechanism may be related to carbamazepine induction of CYP450 enzymes responsible for tricyclic antidepressant metabolism. Serum concentrations of carbamazepine may be increased by an unknown mechanism. Additionally, tricyclic antidepressants may counteract the anticonvulsant effects of carbamazepine by lowering the seizure threshold.', NULL, 'Clinical monitoring of serum concentrations, patient response, seizure control, and tolerance is recommended whenever either agent is added to or discontinued from an existing regimen, or when the dosage is changed.', NULL, 'Clinical monitoring of serum concentrations, patient response, seizure control, and tolerance is recommended whenever either agent is added to or discontinued from an existing regimen, or when the dosage is changed.', 'Metabolism', 'Oxitriptan, Tryptophan, Milnacipran, Levomilnacipran', 'Lacosamide', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56510/', '[1] Brown CS, Wells BG, Cold JA, Froemming JH, Self TH, Jabbour JT "Possible influence of carbamazepine on plasma imipramine concentrations in children with attention deficit hyperactivity disorder." J Clin Psychopharmacol 10 (1990): 359-62[2] Brosen K, Kragh-Sorensen P "Concomitant intake of nortriptyline and carbamazepine." Ther Drug Monit 15 (1993): 258-60[3] Jerling M, Bertilsson L, Sjoqvist F "The use of therapeutic drug monitoring data to document kinetic drug interactions: an example with amitriptyline and nortriptyline." Ther Drug Monit 16 (1994): 1-2[4] Le