INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56467, 'Carbamazepine', 'Pazopanib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and pharmacologic effects of pazopanib, which is primarily metabolized by the isoenzyme.', NULL, 'The use of pazopanib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St.', NULL, 'The use of pazopanib in combination with potent CYP450 3A4 inducers such as carbamazepine, dexamethasone, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort should generally be avoided. Alternative treatment lacking CYP450 3A4-inducing activity should be considered in patients receiving pazopanib; otherwise, pazopanib should not be used. Other known CYP450 3A4 inducers include aminoglutethimide, barbiturates, bexarotene, bosentan, dabrafenib, efavirenz, nafcillin, nevirapine, somatrem, somatropin, and various other anticonvulsants, although the extent to which they may interact with pazopanib is unknown.', 'Metabolism', 'Brivaracetam, Topiramate, Lamotrigine, Ethosuximide, Lacosamide, Levetiracetam, Tiagabine, Ethotoin, Rufinamide, Clonazepam, Paramethadione, More', 'Alectinib, Lenvatinib, Trametinib, Dacomitinib', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56539/', '[1] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Votrient (pazopanib)." GlaxoSmithKline, Research Triangle Park, NC.[3] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[4] Skillman TG, Feldman JM "The pharmacolo