INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56846, 'Phenelzine', 'Levodopa', 'Major', 'Levodopa is the precursor of dopamine, which in turn is converted to norepinephrine. As such, levodopa may precipitate severe hypertensive reactions in patients treated with nonselective monoamine oxidase inhibitors (MAOIs). The mechanism is enhanced peripheral catecholamine availability due to decreased degradation (MAOI activity) and increased synthesis (levodopa effect) of dopamine and probably also norepinephrine.', NULL, 'The concomitant administration of carbidopa has been reported to prevent or blunt the hypertensive response by inhibiting peripheral conversion of levodopa to dopamine.', NULL, 'The concomitant administration of carbidopa has been reported to prevent or blunt the hypertensive response by inhibiting peripheral conversion of levodopa to dopamine. However, some clinicians maintain that, with or without carbidopa, levodopa should not be used concurrently with nonselective MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with levodopa.', 'Synergism', 'Oxitriptan, Viloxazine, Vortioxetine, Milnacipran, Tryptophan, Levomilnacipran, Vilazodone', 'Amantadine, Cabergoline', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56922/', '[1] "Product Information. Sinemet (carbidopa-levodopa)." DuPont Pharmaceuticals, Wilmington, DE.[2] Hunter KR, Boakes AJ, Laurence DR, Stern GM "Monoamine oxidase inhibitors and L-dopa." Br Med J 3 (1970): 388[3] Teychenne PF, Calne DB, Lewis PJ, Findley LJ "Interactions of