INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28501, 'Aripiprazole', 'Apomorphine', 'Moderate', 'Coadministration with dopamine antagonists (e.g., neuroleptic agents, metoclopramide) may decrease the effectiveness of apomorphine, which is a dopaminergic agent. In addition, some of these agents as well as apomorphine have been associated with sedation, hypotension, and QT interval prolongation. Additive pharmacodynamic effects may occur when used in combination. Excessive prolongation of the QT interval can increase the risk of ventricular arrhythmias including torsade de pointes and sudden death in susceptible patients.', NULL, 'The use of apomorphine in combination with dopamine antagonists should generally be avoided unless the potential benefits outweigh the risks.', NULL, 'The use of apomorphine in combination with dopamine antagonists should generally be avoided unless the potential benefits outweigh the risks. Monitoring for altered efficacy of apomorphine and increased side effects such as sedation or hypotension is recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Also, they should avoid activities requiring mental alertness until they know how these agents affect them. Patients who experience increased episodes of falling asleep during normal daily activities should avoid driving and other potentially hazardous activities until they have been evaluated by their physician.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28554/', '[1] "Product Information. Apokyn (apomorphine)." Mylan Pharmaceuticals Inc, Morgantown, WV.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28502, 'Arsenic trioxide', 'Apomorphine', 'Major', 'Arsenic trioxide can cause QT interval prolongation and complete atrioventricular block. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'If possible, medications that are known to prolong the QT interval should be discontinued prior to initiating therapy with arsenic trioxide and withheld for at least several weeks after completion of therapy.', NULL, 'If possible, medications that are known to prolong the QT interval should be discontinued prior to initiating therapy with arsenic trioxide and withheld for at least several weeks after completion of therapy. Caution is advised if concomitant use cannot be avoided. Patients should have frequent ECGs and be monitored for arrhythmias when QT interval is prolonged. An absolute QT interval exceeding 500 msec will require immediate action to correct concomitant risk factors, if any, as well as a thorough assessment of the need for continued therapy. Patients who develop syncope or arrhythmia should be hospitalized for clinical and laboratory monitoring.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28555/', '[1] "Product Information. Trisenox (arsenic trioxide)" Cephalon Inc, West Chester, PA.[2] Ohnishi K, Yoshida H, Shigeno K, et al. "Prolongation of the QT interval and ventricular tachycardia in patients treated with arsenic trioxide for acute promyelocytic leukemia." Ann Intern Med 133 (2000): 881-5[3] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28503, 'Asenapine', 'Apomorphine', 'Moderate', 'Asenapine may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', NULL, 'Coadministration of asenapine with other drugs that can prolong the QT interval should generally be avoided.', NULL, 'Coadministration of asenapine with other drugs that can prolong the QT interval should generally be avoided. Caution and clinical monitoring are recommended if concomitant use is required. Patients should have regular ECGs and be monitored for arrhythmias when QT interval is prolonged.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28556/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[3] "Product Information. Saphris (asenapine)." Schering-Plough Corporation, Kenilworth, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] "Product Information. Pletal (cilostazol)." Otsuka American Pharmaceuticals, Rockville, MD.[7] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[8] Gilman AG, Rall