INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28601, 'Donepezil', 'Apomorphine', 'Moderate', 'Acetylcholinesterase inhibitors may have vagotonic effects on the sinoatrial and atrioventricular nodes, which occasionally manifest as bradycardia or heart block (<2%). Because bradycardia is a risk factor for torsade de pointes, a theoretical risk exists when combined with agents that prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval.', NULL, 'Caution is advised when acetylcholinesterase inhibitors are used with drugs that can prolong the QT interval. Patients should be monitored for bradycardia, atrioventricular block and syncope, and advised to seek medical attention if they experience dizziness, lightheadedness, fainting, shortness of breath, or slow or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28654/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Exelon (rivastigmine)" Novartis Pharmaceuticals, East Hanover, NJ.[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Aricept (donepezil)." Pfizer US Pharmaceuticals, New York, NY.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28602, 'Doxazosin', 'Apomorphine', 'Moderate', 'Apomorphine may potentiate the hypotensive effect of vasodilators and antihypertensive agents. Apomorphine alone has been associated with orthostatic hypotension, hypotension, syncope, and dose-dependent decreases in systolic blood pressure.', NULL, 'Caution and close monitoring for altered efficacy and safety are recommended if patients receive apomorphine with an antihypertensive agent or vasodilator.', NULL, 'Caution and close monitoring for altered efficacy and safety are recommended if patients receive apomorphine with an antihypertensive agent or vasodilator. Patients should be made aware of the possible side effects (e.g., dizziness, lightheadedness, orthostasis) and be cautioned about driving, operating machinery, or performing other hazardous tasks. They should also be advised to avoid rising abruptly from a sitting or recumbent position and to contact their physician if they experience symptoms of hypotension such as dizziness, lightheadedness, or fainting.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28655/', '[1] "Product Information. Apokyn (apomorphine)." Mylan Pharmaceuticals Inc, Morgantown, WV.[2] Bailey DG, Dresser GK "Natural products and adverse drug interactions." Can Med Assoc J 170 (2004): 1531-2[3] Ho PC, Ghose K, Saville D, Wanwimolruk S "Effect of grapefruit juice on pharmacokinetics and pharmacodynamics of verapamil enantiomers in healthy volunteers." Eur J Clin Pharmacol 56 (2000): 693-8[4] Fuhr U, Muller-Peltzer H, Kern R, et al. "Effects of grapefruit juice and smoking on verapamil concentrations in steady