INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28771, 'Olanzapine', 'Apomorphine', 'Moderate', 'Coadministration with dopamine antagonists (e.g., neuroleptic agents, metoclopramide) may decrease the effectiveness of apomorphine, which is a dopaminergic agent. In addition, some of these agents as well as apomorphine have been associated with sedation, hypotension, and QT interval prolongation. Additive pharmacodynamic effects may occur when used in combination. Excessive prolongation of the QT interval can increase the risk of ventricular arrhythmias including torsade de pointes and sudden death in susceptible patients.', NULL, 'The use of apomorphine in combination with dopamine antagonists should generally be avoided unless the potential benefits outweigh the risks.', NULL, 'The use of apomorphine in combination with dopamine antagonists should generally be avoided unless the potential benefits outweigh the risks. Monitoring for altered efficacy of apomorphine and increased side effects such as sedation or hypotension is recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Also, they should avoid activities requiring mental alertness until they know how these agents affect them. Patients who experience increased episodes of falling asleep during normal daily activities should avoid driving and other potentially hazardous activities until they have been evaluated by their physician.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28824/', '[1] "Product Information. Apokyn (apomorphine)." Mylan Pharmaceuticals Inc, Morgantown, WV.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28772, 'Oliceridine', 'Apomorphine', 'Moderate', 'Central nervous system (CNS) depressant effects may be additively or synergistically increased in patients using apomorphine in combination with other drugs that can also cause these effects. Apomorphine alone has been frequently associated with somnolence and dizziness. Patients may suddenly fall asleep during activities of daily living.', NULL, 'The use of other sedating drugs should generally be avoided during apomorphine treatment.', NULL, 'The use of other sedating drugs should generally be avoided during apomorphine treatment. Patients prescribed these agents concurrently should be monitored for potentially excessive or prolonged CNS depression, especially if they are elderly or debilitated. Ambulatory patients should be made aware of the possibility of additive CNS effects (e.g., drowsiness, dizziness, lightheadedness, confusion) and counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28825/', '[1] "Product Information. Apokyn (apomorphine)." Mylan Pharmaceuticals Inc, Morgantown, WV.[2] "Product Information. Zohydro ER (HYDROcodone)." Zogenix, Inc, San Diego, CA.', 1767363020);
