INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28788, 'Ozanimod', 'Apomorphine', 'Major', 'Due to the risk of bradycardia and atrioventricular (AV) block, the risk of QT prolongation and torsade de pointes arrhythmia may be increased during initiation of ozanimod treatment in patients receiving drugs that prolong the QT interval.', NULL, 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval.', NULL, 'Ozanimod has not been studied in patients receiving drugs that can prolong the QT interval. Because bradycardia and AV block are recognized risk factors for QT prolongation and torsade de pointes arrhythmia, treatment with ozanimod should generally not be initiated in patients who are concurrently treated with QT prolonging drugs with known arrhythmogenic properties.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28841/', '[1] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.[2] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.[3] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[4] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (28789, 'Paliperidone', 'Apomorphine', 'Moderate', 'Coadministration with dopamine antagonists (e.g., neuroleptic agents, metoclopramide) may decrease the effectiveness of apomorphine, which is a dopaminergic agent. In addition, some of these agents as well as apomorphine have been associated with sedation, hypotension, and QT interval prolongation. Additive pharmacodynamic effects may occur when used in combination. Excessive prolongation of the QT interval can increase the risk of ventricular arrhythmias including torsade de pointes and sudden death in susceptible patients.', NULL, 'The use of apomorphine in combination with dopamine antagonists should generally be avoided unless the potential benefits outweigh the risks.', NULL, 'The use of apomorphine in combination with dopamine antagonists should generally be avoided unless the potential benefits outweigh the risks. Monitoring for altered efficacy of apomorphine and increased side effects such as sedation or hypotension is recommended if concomitant use is required. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Also, they should avoid activities requiring mental alertness until they know how these agents affect them. Patients who experience increased episodes of falling asleep during normal daily activities should avoid driving and other potentially hazardous activities until they have been evaluated by their physician.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/28842/', '[1] "Product Information. Apokyn (apomorphine)." Mylan Pharmaceuticals Inc, Morgantown, WV.', 1767363020);
