INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (29328, 'Bexarotene', 'Aprepitant', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 may theoretically increase the plasma concentrations of bexarotene. In vitro studies suggest that bexarotene is metabolized by CYP450 3A4. However, concomitant administration with multiple doses of ketoconazole, a potent CYP450 3A4 inhibitor, did not alter bexarotene plasma concentrations, which would imply that bexarotene elimination is not substantially dependent on CYP450 3A4 metabolism in vivo. Conversely, bexarotene appears to be an inducer of CYP450 3A4 and may reduce the plasma concentrations of CYP450 3A4 inhibitors that are also substrates of the isoenzyme, such as azole antifungals, macrolides, some calcium channel blockers, mifepristone, and nefazodone.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/29381/', '[1] "Product Information. Targretin (bexarotene)" Ligand Pharmaceuticals, San Diego, CA.[2] "Product Information. Targretin (bexarotene)" Ligand Pharmaceuticals, San Diego, CA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363020);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (29329, 'Bicalutamide', 'Aprepitant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of aprepitant, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors.', NULL, 'Caution is advised if aprepitant is prescribed in combination with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, clarithromycin, conivaptan, idelalisib, nefazodone, telithromycin, cobicistat, delavirdine, and protease inhibitors. Pharmacologic response to aprepitant should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with aprepitant.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/29382/', '[1] "Product Information. Emend for Injection (fosaprepitant)." Merck & Company Inc, West Point, PA.[2] "Product Information. Emend (aprepitant)." Merck & Company Inc, West Point, PA.', 1767363020);
