INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (29615, 'Telotristat ethyl', 'Aprepitant', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes. The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/29668/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.[3] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (29616, 'Temsirolimus', 'Aprepitant', 'Moderate', 'Coadministration of temsirolimus with inhibitors of CYP450 3A4 may increase the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', NULL, 'Caution is advised if temsirolimus is prescribed in combination with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if temsirolimus is prescribed in combination with CYP450 3A4 inhibitors. Pharmacologic response to temsirolimus should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the temsirolimus dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/29669/', '[1] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (29617, 'Teniposide', 'Aprepitant', 'Moderate', 'Coadministration with aprepitant or its prodrug, fosaprepitant, may increase the plasma concentrations of chemotherapeutic agents that are primarily metabolized by CYP450 3A4. The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by aprepitant. In general, the effect of aprepitant on the pharmacokinetics of CYP450 3A4 substrates is expected to be greater when the substrates are administered orally as opposed to intravenously and may be altered following prolonged administration.', NULL, 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials.', NULL, 'Caution is advised if aprepitant or fosaprepitant is administered with chemotherapeutic agents that are primarily metabolized by CYP450 3A4, particularly those that were not studied extensively in premarketing trials. The potential for increased systemic toxicities of these agents should be considered. Chronic, continuous use of aprepitant for prevention of nausea and vomiting is not recommended because it has not been studied and because the drug interaction profile may change during long-term use.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/29670/', '[1] Charasson V, Haaz MC, Robert J "Determination of Drug Interactions Occurring with the Metabolic Pathways of Irinotecan." Drug Metab Dispos 30 (2002): 731-733[2] Zhou XJ, Zhou-Pan XR, Gauthier T, Placidi M, Maurel P, Rahmani R "Human liver micr