INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (29663, 'Desogestrel', 'Atorvastatin', 'Minor', 'Coadministration with atorvastatin may increase the plasma concentrations of estrogens and progestins. The exact mechanism of interaction is unknown but may be related to competitive inhibition of CYP450 3A4, the isoenzyme responsible for the metabolism of atorvastatin, estrogens, some (if not all) progestins, and other steroids. The clinical significance of these alterations is unknown but should be considered when selecting oral contraceptive and hormone replacement doses.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/29716/', '[1] "Product Information. Lipitor (atorvastatin)." Parke-Davis, Morris Plains, NJ.[2] Richter WO, Jacob BG, Schwandt P "Interaction between fibre and lovastatin." Lancet 338 (1991): 706[3] "Product Information. Lipitor (atorvastatin)." Parke-Davis, Morris Plains, NJ.[4] Boberg M, Angerbauer R, Fey P, Kanhai WK, Karl W, Kern A, Ploschke J, Radtke M "Metabolism of cerivastatin by human liver microsomes in vitro. Characterization of primary metabolic pathways and of cytochrome P45 isozymes involved." Drug Metab Dispos 25 (1997): 321-31[5] McMillan K "Considerations in the formulary selection of hydroxymethylglutaryl coenzyme a reductase inhibitors." Am J Health Syst Pharm 53 (1996): 2206-14[6] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[7] Neuvonen PJ, Backman JT, Niemi M "Pharmacokinetic comparison of the potential over-the-counter statins simvastatin, lovastatin, fluvastatin and pravastatin." Clin Pharmacokinet 47 (2008