INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (29922, 'Droxidopa', 'Arformoterol', 'Moderate', 'Agents that increase blood pressure such as vasopressors, triptans, decongestants, beta-2 agonists, and amphetamines may potentiate the risk of supine hypertension associated with the use of droxidopa in the treatment of neurogenic orthostatic hypotension.', NULL, 'Supine blood pressure should be monitored prior to and during droxidopa treatment, and more frequently when increasing dosage.', NULL, 'Supine blood pressure should be monitored prior to and during droxidopa treatment, and more frequently when increasing dosage. Patients should elevate the head of the bed when resting or sleeping, and have blood pressure also measured in this position. Dosage reduction or discontinuation of droxidopa is recommended if supine hypertension cannot be managed by elevation of the head of the bed. Supine hypertension that is not well managed may increase the risk of cardiovascular events, particularly stroke.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/29975/', '[1] "Product Information. Northera (droxidopa)." Chelsea Therapeutics Inc, Charlotte, NC.', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (29923, 'Duloxetine', 'Arformoterol', 'Moderate', 'Additive or synergistic effects on blood pressure and heart rate may occur when serotonin-norepinephrine reuptake inhibitors (SNRIs) are combined with sympathomimetic agents such as nasal decongestants, adrenergic bronchodilators, ophthalmic vasoconstrictors, and systemic vasopressors. The use of SNRIs alone has been associated with sustained increases in blood pressure and heart rate, and cases of elevated blood pressure requiring immediate treatment have been reported in postmarketing experience.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate.', NULL, 'Caution is advised if SNRIs are used with other drugs that can increase blood pressure and/or heart rate. Blood pressure and pulse should be measured prior to initiating SNRI therapy and monitored at regular intervals thereafter. Dose reduction or discontinuation of the SNRI should be considered in patients who experience a sustained increase in blood pressure or pulse rate.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/29976/', '[1] "Product Information. Fetzima (levomilnacipran)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Cymbalta (duloxetine)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Nucynta (tapentadol)." PriCara Pharmaceuticals, Raritan, NJ.[4] "Product Information. Effexor (venlafaxine)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[5] "Product Information. Pristiq (desvenlafaxine)." Wyeth Laboratories, Philadelphia, PA.[6] "Product Information. Savella (milnacipran)." Forest Pharmaceuticals, St. Louis, MO.[7] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[8] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[9] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[10] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[11] "Grapefruit juice interactions with drugs." Med Lett Drugs Ther 37 (1995): 73-4[12] Maish WA, Hampton EM, Whitsett TL, Shepard JD, Lovallo WR "Influence of grapefruit juice on caffeine pharmacokinetics and pharmacodynamics." Pharmacotherapy 16 (1996): 1046-52', 1767365203);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (29924, 'Dyphylline', 'Arformoterol', 'Moderate', 'Concomitant use of beta-2 adrenergic agonists with theophylline may increase the risk and/or severity of hypokalemia and adverse cardiovascular effects such as palpitation, tachycardia, and blood pressure elevation. Adverse events, especially hypokalemia, may be more likely with systemic or nebulized formulations of beta-2 agonists or high dosages of theophylline. Beta-2 agonists can cause clinically significant but usually transient decreases in serum potassium concentrations, while hypokalemia associated with theophylline often occurs in toxicity. Pharmacokinetically, some beta-2 agonists given systemically may decrease the plasma concentrations of theophylline.', NULL, 'Although theophylline and beta-2 agonists are commonly used together to produce bronchodilation, it may be appropriate to monitor patient response as well as serum potassium level, blood pressure and heart rate during coadministration, especially if the beta-2 agonist is administered systemically or by nebulizer.', NULL, 'Although theophylline and beta-2 agonists are commonly used together to produce bronchodilation, it may be appropriate to monitor patient response as well as serum potassium level, blood pressure and heart rate during coadministration, especially if the beta-2 agonist is administered systemically or by nebulizer. Close monitoring is particularly important in patients with severe asthma, since the potential increases in blood pressure and heart rate may have more serious consequences in the presence of hypoxemia or hypercapnia due to increased myocardial oxygen consumption. Patients should be advised to notify their physician 