INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341418, 'Almotriptan', 'Zolmitriptan', 'Major', 'Coadministration of two or more 5-HT1 receptor agonists and/or ergotamine-containing or ergot-type medications may potentiate the risk of vasospastic reactions, including coronary artery vasospasm, peripheral vascular ischemia and colonic ischemia, due to additive pharmacologic effects.', 'DDInter', 1767317475, 'The concomitant use of two or more 5-HT1 receptor agonists and/or ergotamine-containing or ergot-type medications, or use within 24 hours of each other, is considered contraindicated.', 'Synergism', 'The concomitant use of two or more 5-HT1 receptor agonists and/or ergotamine-containing or ergot-type medications, or use within 24 hours of each other, is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
