INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341435, 'Alogliptin', 'Bexarotene', 'Major', 'Coadministration with other drugs that are known to increase triglyceride levels or cause pancreatic toxicity may potentiate the risk of pancreatitis associated with the use of bexarotene.', 'DDInter', 1767317475, 'Caution is advised when bexarotene is given in combination with other medications that are known to increase triglyceride levels or associated with pancreatic toxicity. Other risk factors such as a history of pancreatitis, uncontrolled hyperlipidemia, excessive alcohol consumption, uncontrolled diabetes mellitus, or biliary tract disease should also be considered when weighing benefits versus risks of bexarotene therapy. All patients treated with bexarotene should have fasting blood lipids measured before initiating therapy, weekly during therapy until the lipid response to bexarotene is established (usually within two to four weeks), and at 8-week intervals thereafter.', 'Synergism', 'Caution is advised when bexarotene is given in combination with other medications that are known to increase triglyceride levels or associated with pancreatic toxicity.', NULL, NULL, 'Major', 'pharmacodynamic');
