INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341449, 'Alogliptin', 'Chromic chloride', 'Moderate', 'Pharmacologic dosages of chromium (200 mcg/day or more for adults) may help improve diabetic control and reduce the requirements for insulin and other antidiabetic agents. Chromium appears to play a role in normal insulin function and glucose utilization.', 'DDInter', 1767317475, 'Until further data are available, therapy with pharmacologic dosages of chromium should be administered cautiously in patients receiving insulin or other antidiabetic agents. Patients should be monitored for changes in diabetic medication requirements.', 'Synergism', 'Until further data are available, therapy with pharmacologic dosages of chromium should be administered cautiously in patients receiving insulin or other antidiabetic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
