INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341462, 'Alogliptin', 'Dabrafenib', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9, including many antidiabetic agents such as nateglinide, repaglinide, linagliptin, saxagliptin, and sulfonylureas.', 'DDInter', 1767317475, 'Caution is advised when dabrafenib is prescribed to patients with diabetes, particularly those receiving antidiabetic agents that are metabolized by CYP450 3A4 and/or 2C9. Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of dabrafenib, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination. Likewise, patients should be observed for hypoglycemia when dabrafenib is withdrawn from their therapeutic regimen.', 'Metabolism', 'Caution is advised when dabrafenib is prescribed to patients with diabetes, particularly those receiving antidiabetic agents that are metabolized by CYP450 3A4 and/or 2C9.', NULL, NULL, 'Moderate', 'pharmacodynamic');
