INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341683, 'Colesevelam', 'Metformin', 'Moderate', 'Coadministration with colesevelam may increase the plasma concentrations of metformin. The mechanism of interaction has not been described.', 'DDInter', 1767317475, 'Caution is advised if metformin is used concomitantly with colesevelam. Clinical response and tolerance to metformin should be monitored more closely following the initiation or discontinuation of colesevelam. Patients should be advised to contact their physician if they experience increased side effects of metformin such as nausea, vomiting, diarrhea, flatulence, indigestion, and asthenia. Immediate medical attention should be sought if they experience potential signs and symptoms of lactic acidosis such as malaise, myalgia, somnolence, abdominal distress, respiratory distress, hyperventilation, slow or irregular heartbeat, and hypothermia.', 'Others', 'Caution is advised if metformin is used concomitantly with colesevelam.', NULL, NULL, 'Moderate', 'pharmacodynamic');
