INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341699, 'Dexfenfluramine', 'Metformin', 'Moderate', 'Since weight loss may affect glycemic control and increase the risk of hypoglycemia in type 2 diabetes mellitus, patients receiving anorectic drugs may require periodic adjustments of their antidiabetic medications.', 'DDInter', 1767317475, 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed. Patients should be apprised of the risk of hypoglycemia and be alert to potential signs and symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.', 'Synergism', 'Blood glucose should be closely monitored during weight loss treatment, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1341700, 'Diatrizoate', 'Metformin', 'Major', 'Administration of intravascular iodinated radiocontrast media in patients treated with biguanides such as metformin may precipitate lactic acidosis, a rare but potentially serious and fatal complication of biguanide therapy. Iodinated contrast material can cause acute alterations in renal function, including acute renal failure, which is a known risk factor for biguanide-induced lactic acidosis.', 'DDInter', 1767317475, 'Biguanide therapy should be temporarily discontinued at the time of, or prior to, radiographic studies involving intravascular administration of iodinated contrast media, and not resumed until at least 48 hours after the procedure and renal function has been re-evaluated and determined to be normal.', 'Excretion', 'Biguanide therapy should be temporarily discontinued at the time of, or prior to, radiographic studies involving intravascular administration of iodinated contrast media, and not resumed until at least 48 hours after the procedure and renal function has been re-evaluated and determined to be normal.', NULL, NULL, 'Major', 'pharmacodynamic');
